import {useEffect, useState} from 'react';

const students =[{

  imgURl: "",
  fullName: "Даукаев Камиль",
  balls: 1110},
  {
    imgURl: "",
    fullName: "Шайхутдинов Лиана",
    balls: -10000}
]

function App() {
  const [student, setStudent] = useState("Шайхутдинвоа Лиана"); 


  console.log(student)

  let num = 1;

  let bla = useEffect (() => {
    fetch("https://pokeapi.co/api/v2/ability/")
  }, []);

  return (
      <>
      {
      students.map(item => <StudentCard student={item} />)
      } 


      {bla}

      <Balls/>
      </>
  );
}


function Balls(props ){
  const [balls, setBalls] = useState(props.balls); 



  const handleAddButton = () => {
    setBalls(balls + 20);

  }

  if (balls < 0) alert("Писец eму");

  return (<>
    <p> <b>Баллы:</b> {balls}</p>
    <button onClick={handleAddButton}>Прибавить баллов</button>
    <button onClick={() => console.log('CLICK')}  ></button>
    </>
  )
}


function StudentCard(props){

  const{ imgURl, fullName, balls} = props.student

  return(  <>
    <img width="150" src="https://sun9-32.userapi.com/impg/XeZYbbMrGV_qDOiA64CUgZ4ibdvRkfTF_C-dlg/gahMDL7WgO0.jpg?size=768x1024&quality=96&sign=3d77d5ce699cb1f6787ad4c8e4c0df20&type=album"></img>
    <p><b>Student: </b>shaihutdinova liana</p>

    </>)

}


export default App;
